import React, { Component } from 'react'

export default class Search extends Component {

    state = {
        contents: [],
        keyword: ""
    };

    handleInputChange = e => {
        this.setState({keyword: e.target.value});
    };

    render() {
        const {keyword} = this.state;
        return (
            <div>
                <form onSubmit>
                    <label>검색</label>
                    <input 
                        type="text" 
                        onChange={this.handleInputChange} 
                        value={keyword}
                    />
                </form>
            </div>
        );
    }
}
